import React, { useState } from "react";
import { Button,FormControl,Input,InputLabel } from "@material-ui/core";
import "./App.css";

function App() {
  const [todo, setTodo] = useState([
    "takes Dogs for walk",
    "take rubiss hrs",
    "I want to live stream this project",
  ]);
  const [input, setInput] = useState("");
  console.log(input);

  const addTodo = (event) => {
    //This will fire off when we click button!
    event.preventDefault();
    console.log("I AM WORKING");
    setTodo([...todo, input]);
    setInput(""); //clear up the input after hitting submit
  };
  return (
    <div className="App">
      <h1>Clever Programmer {1 + 1}</h1>
      <from>

        <FormControl>
          <InputLabel>Write a ToDo</InputLabel>
          <input  value={input}
          onChange={(event) => setInput(event.target.value)}/>
        </FormControl>
        <Button
          disabled={!input}
          type="submit"
          onClick={addTodo}
          variant="contained"
          color="primary"
        >
          ToDo list
        </Button>
        {/* <button type="submit" onClick={addTodo}>
          ToDo list{" "}
        </button> */}
      </from>

      <ul>
        {todo.map((todo) => (
          <li>{todo}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
